INSERT INTO PanelsForGame_locations (id, name, description) VALUES (1, 'Аэропорт', 'Сталкеры прозвали так эту Зону из-за наличия в её центре большого, еще частично рабочего, аэропорта.');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (2, 'Везувий', 'Эту Зону так прозвали из-за внезапно возникшего вулкана, в честь некогда так же внезапно пробудившегося и унесшего много жизней.');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (3, 'Город N', 'Локация, доступ к которой возможен только в новогоднем обновлении.');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (4, 'Жерло', 'Как только событие будет запущено на всю локацию Везувий раздастся соответствующее звуковое уведомление, а на локацию Жерло проход будет закрыт.');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (5, 'Заброшенный склад', 'Стартовая локация. На ней происходят Ваши первые шаги и обучение. Вы учитесь выполнять задания, убивать противников и мутантов, экипировать оружие и броню, вам объясняют составляющую механики и геймплея игры.');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (6, 'Караван', 'Караван');
INSERT INTO PanelsForGame_locations (id, name, description) VALUES (7, 'Любеч', 'Военный городок - первое пристанище всех бродяг и сталкеров.');
